import React from 'react'
import './Checkout.css'
import Subtotal from "./Subtotal"
import CheckoutProduct from './CheckoutProduct'
import { useStateValue } from './StateProvider'

function Checkout() {
  const [{basket}, dispatch] = useStateValue();
  return (
    <div className="checkout">

        <div className="checkout_left">
            <img className="checkout_ad" src="https://wp.disruptiveadvertising.com/wp-content/uploads/2018/10/Screen-Shot-2018-10-29-at-11.50.03-AM-768x164.png"/>
            <div>
                <h2 className="checkout_title">Your Shopping Cart</h2>  
                {basket.map(item => (
                  <CheckoutProduct
                  id={item.id}
                  title={item.title}
                  image = {item.image}
                  price = {item.price}
                  rating = {item.rating}
                  />
                ))}
            </div>
        </div>

        <div className="checkout_right">
            <Subtotal />
            <img className="checkout_ad2" src="http://www.maurobartoccelli.com/wp-content/uploads/2017/04/mcdonalds_ad2.jpg"/>
        </div>

    </div>
  )
}

export default Checkout
