import React from 'react'
import './Checkout.css'
import Subtotal from "./Subtotal"
import CheckoutProduct from './CheckoutProduct'
import { useStateValue } from './StateProvider'

function Checkout() {
  const [{basket}, dispatch] = useStateValue();
  let value = "Your Shopping Cart"
  if({basket}.length==0){
    value = "Your Shopping Cart Is Empty"
  }
  return (
    <div className="checkout">

        <div className="checkout_left">
            <img className="checkout_ad" src="https://wp.disruptiveadvertising.com/wp-content/uploads/2018/10/Screen-Shot-2018-10-29-at-11.50.03-AM-768x164.png"/>
            <div>
                <h2 className="checkout_title">{value}</h2>  
                {basket.map(item => (
                  <CheckoutProduct
                  id={item.id}
                  title={item.title}
                  image = {item.image}
                  price = {item.price}
                  rating = {item.rating}
                  />
                ))}
            </div>
        </div>

        <div className="checkout_right">
            <Subtotal />
        </div>

    </div>
  )
}

export default Checkout
